import React, { Component } from "react";
import "./App.css";

class App extends Component {

  state = {
    notes: [],
    currentNote: ""
  };


  render() {
    return (
      <div className="App">
        <h1>localStorage Note-Taking Demo</h1>

        <textarea className="input" placeholder="Notes" />
        <br/>
        <button className="button">Submit</button>
      </div>
    );
  }
}

export default App;
