  render() {
    return (
      <div className="App">
        <h1>localStorage Note-Taking Demo</h1>

        {this.state.objList.name}

        <textarea
          onChange={event => this.setState({ currentNote: event.target.value })}
          value={this.state.currentNote}
          className="input"
          placeholder="Notes"
        />
        <br/>
        <button className="button" onClick={this.addNote}>
          Submit
        </button>

        {
          this.state.objList.map((objList, index) => (
            <div key={index}>
              {objList}
              <button onClick={() => this.deleteNote(index)}>Delete</button>
            </div>
          ))
        }
      </div>
    );
  }
}

export default App;
